(()=>{$(document).ready(function(){$.getJSON("/index.json",function(n){let s=$("#search-results");n.forEach(function(e){let t='<div><a class="font-large" href="'+e.url+'">'+e.title+'</a><p class="article-description">'+e.body+"</p></div>";s.append(t)});function r(e){let t=[],l=new RegExp(e.replace(/^([▽▼])/,""),"i");for(i=0;i<n.length;i++){let a=n[i].body.search(l);a!=-1&&t.push({data:n[i],pos:a})}return t}$("#search-input").on("input",function(){s.empty();let e=$(this).val(),t=r(e);t.length>0?t.forEach(function(l){let a='<p class="article-description">'+l.data.body.slice(l.pos)+"</p>",c="<div>"+('<a class="font-large" href="'+l.data.url+'">'+l.data.title+"</a>")+a+"</div>";s.append(c)}):e.length>0&&s.append("<li>\u691C\u7D22\u7D50\u679C\u306F\u3042\u308A\u307E\u305B\u3093</li>")})})});})();
